sourcesContent:
const Role = require('../models/role')
const Order_State = require('../models/orderstate')



   let createRecordsDefault = async () => {
        try {
            roleAdmin = await Role.findOne({ where: { roleID: 1 } })
            if (roleAdmin == null) await Role.create({ roleID: 1, roleName: 'admin' })

            roleCustomer = await Role.findOne({ where: { roleID: 2 } })
            if (roleCustomer == null) await Role.create({ roleID: 2, roleName: 'customer' })

            state1 = await Order_State.findOne({ where: { orderStateID: 1 } })
            if (state1 == null) await Order_State.create({ orderStateID: 1, orderStateName: 'Chờ Xác Nhận' })

            state2 = await Order_State.findOne({ where: { orderStateID: 2 } })
            if (state2 == null) await Order_State.create({ orderStateID: 2, orderStateName: 'Đã Xác Nhận' })

            state3 = await Order_State.findOne({ where: { orderStateID: 3 } })
            if (state3 == null) await Order_State.create({ orderStateID: 3, orderStateName: 'Đang Vận Chuyển' })

            state4 = await Order_State.findOne({ where: { orderStateID: 4 } })
            if (state4 == null) await Order_State.create({ orderStateID: 4, orderStateName: 'Đã Giao' })

            state5 = await Order_State.findOne({ where: { orderStateID: 5 } })
            if (state5 == null) await Order_State.create({ orderStateID: 5, orderStateName: 'Đã Hủy' })

            state6 = await Order_State.findOne({ where: { orderStateID: 6 } })
            if (state6 == null) await Order_State.create({ orderStateID: 6, orderStateName: 'Hủy Bởi Shop' })
        } catch (err) {
            console.log(err)
        }
    }
module.exports= createRecordsDefault;

