sourcesContent:
const bcrypt = require('bcrypt');
const User = require('../models/user');
const Admin = require('../models/admin');

let register = async (req, res, next) => {
  let email = req.body.email;
  if (email === undefined) return res.status(400).send('Trường email không tồn tại');

  let user = await User.findOne({ where: { email , roleID:1 } });
  if (user) return res.status(409).send("Email đã tồn tại");

  try {
    let hashPassword = bcrypt.hashSync(req.body.password, 10);
    let newUser = { email: email, password: hashPassword,roleID : 1 };
    let createUser = await User.create(newUser);

    let newAdmin = { userID: createUser.id };
    let createdAdmin = await Admin.create(newAdmin);

    return res.send({ user: createdUser, admin: createdAdmin });
  } catch (err) {
    console.log(err);
    return res.status(400).send("Có lỗi trong quá trình tạo tài khoản vui lòng thử lại");
  }
}
let login = async (req, res, next) => {
    let email = req.body.email;
    if (email === undefined) return res.status(400).send('Trường email không tồn tại');
  
    let password = req.body.password;
    if (password === undefined) return res.status(400).send('Trường password không tồn tại');
  
    try {
      let admin = await User.findOne({
        where: { email, roleID:1 },
        attributes: ['userID', 'firstName', 'lastName', 'email', 'address', 'phone', 'roleID','password'],
        include: [
          {
            model: Admin,
            attributes: ['adminID'],
            required: true,
          },
        ],
      });
  
      if (!admin || admin.roleID !== 1) {
        return res.status(401).send("Email không chính xác hoặc không phải tài khoản quản trị viên");
      }
  
      let isPasswordValid = bcrypt.compareSync(password, admin.password);
      if (!isPasswordValid) {
        return res.status(401).send("Mật khẩu không chính xác");
      }
  
      return res.send({
        adminID: admin.Admin.adminID,
        userID: admin.userID,
        
        email: admin.email,
        address: admin.address,
        phone: admin.phone,
        
      });
    } catch (err) {
      console.log(err);
      return res.status(400).send("Có lỗi trong quá trình đăng nhập vui lòng thử lại");
    }
  }
  module.exports = {
    register,
    login, 
  }

