    let newAdmin = { userID: createUser.id };
    let createdAdmin = await Admin.create(newAdmin);

    return res.send({ user: createdUser, admin: createdAdmin });
  } catch (err) {
    console.log(err);
    return res.status(400).send("Có lỗi trong quá trình tạo tài khoản vui lòng thử lại");
  }
}
let login = async (req, res, next) => {
