const bcrypt = require('bcrypt');
const User = require('../models/user');
const Customer = require('../models/customer');

let register = async (req, res, next) => {
  let email = req.body.email;
  if (email === undefined) return res.status(400).send('Trường email không tồn tại');

  let user = await User.findOne({ where: { email, role_id: 2 } });
  if (user) return res.status(409).send("Email đã tồn tại");

  try {
    let hashPassword = bcrypt.hashSync(req.body.password, 10);
    let newUser = { email: email, password: hashPassword, role_id: 2 };
    let createUser = await User.create(newUser);

    let newCustomer = { userID: createUser.id };
    let createCustomer = await Customer.create(newCustomer);

    return res.send({ user: createUser, customer: createCustomer });
  } catch (err) {
    console.log(err);
    return res.status(400).send("Có lỗi trong quá trình tạo tài khoản vui lòng thử lại");
  }
}
let login = async (req, res, next) => {
    let email = req.body.email;
    if (email === undefined) return res.status(400).send('Trường email không tồn tại');
  
    let password = req.body.password;
    if (password === undefined) return res.status(400).send('Trường password không tồn tại');
  
    try {
      let customer = await User.findOne({
        where: { email, roleID: 2 },
        attributes: ['userID', 'firstName', 'lastName', 'email', 'address', 'phone', 'roleID','password'],
        include: [
          {
            model: Customer,
            attributes: ['customerID'],
            required: true,
          },
        ],
      });
  
      if (!customer || customer.roleID !== 2) { 
        return res.status(401).send("Email không chính xác hoặc không phải tài khoản khách hàng");
      }
  
      let isPasswordValid = bcrypt.compareSync(password, customer.password);
      if (!isPasswordValid) {
        return res.status(401).send("Mật khẩu không chính xác");
      }
  
      return res.send({
        customerID: customer.Customer.customerID,
        userID: customer.userID,
       
        email: customer.email,
        address: customer.address,
        phone: customer.phone,
      });
    } catch (err) {
      console.log(err);
      return res.status(400).send("Có lỗi trong quá trình đăng nhập vui lòng thử lại");
    }
  }
  let update = async (req, res, next) => {
    
      let userId = req.body.userId;
      if (!userId) return res.status(400).send('Trường userId không tồn tại');
    
      let firstName = req.body.firstName;
      let lastName = req.body.lastName;
      let email = req.body.email;
      let address = req.body.address;
     let password = req.body.password;
    
      try {
        const user = await User.findOne({ where: { userId, roleId: 2 } });
        if (!user) return res.status(409).send("Customer không tồn tại");
    
        const [updatedUser, updatedCustomer] = await sequelize.transaction(async (t) => {
          const updatedUser = await user.update(
            { firstName, lastName, email, address, password },
            { transaction: t }
          );
    
          const customer = await Customer.findOne({ where: { userId } }, { transaction: t });
          const updatedCustomer = await customer.update({ customerId: customer.customerId }, { transaction: t });
    
          return [updatedUser, updatedCustomer];
        });
    
        if (updatedUser && updatedCustomer) {
          return res.send({ firstName, lastName, email, address });
        } else {
          return res.status(400).send("Có lỗi trong quá trình cập nhật vui lòng thử lại");
        }
      } catch (err) {
        console.log(err);
        return res.status(400).send("Có lỗi trong quá trình cập nhật vui lòng thử lại");
      };
    
  }
  module.exports = {
    register,
    login, 
    update
  }

