    foreignKey: { name: 'userID', type: DataTypes.INTEGER, allowNull: false, unique: true  }
});
User.hasOne(CustomerInfo, {
    foreignKey: { name: 'userID', type: DataTypes.INTEGER, allowNull: false, unique:true  }
});
module.exports = CustomerInfo;

