sourcesContent:
const Size = require('../models/size');

let create = async (req, res, next) => {
    let sizeName = req.body.sizeName;
    if(sizeName === undefined) return res.status(400).send('Trường tên size không tồn tại');
    let size = await Size.findOne({ where: { sizeName } });
    if(size) return res.status(409).send('Tên size đã tồn tại');
    else {
        let newSize = await Size.create({ sizeName });
        return res.send(newSize);
    }
}

let list = async (req, res, next) => {
    let sizes = await Size.findAll({ 
        attributes: ['sizeID', 'sizeName'],  
        raw: true 
    });
    return res.send(sizes);
}

module.exports = {
    create,
    list,
};

