sourcesContent:
const Colour = require('../models/colour');

let create = async (req, res, next) => {
    let colourName = req.body.colourName;
    if(colourName === undefined) return res.status(400).send('Trường tên màu không tồn tại');
    let colour = await Colour.findOne({ where: { colourName } });
    if(colour) return res.status(409).send('Tên màu đã tồn tại');
    else {
        let newColour = await Colour.create({ colourName });
        return res.send(newColour);
    }
}

let list = async (req, res, next) => {
    let colours = await Colour.findAll({ 
        attributes: ['colourID', 'colourName'],  
        raw: true 
    });
    return res.send(colours);
}

module.exports = {
    create,
    list,
};

